// A primitive, undocumented convenience class -- who wrote this anyways?import java.io.*;import java.awt.*;public class MyWriter {    PrintWriter ps;        public MyWriter() {        FileDialog fd = new FileDialog(new Frame(), "Where would you like it?", FileDialog.SAVE);        fd.setFile("outJunk");        fd.show();        String filename = fd.getDirectory()+fd.getFile();        try {            ps = new PrintWriter(new FileWriter(filename));        } catch(Exception e) {System.out.print("PW1: No such file! " + e);}}        public MyWriter(String f) {        try {            ps = new PrintWriter(new FileWriter(f));        } catch(Exception e) {System.out.print("PW2: No such file! " + e);}    }        public void print(String s) {        ps.print(s);    }        public void println(String s) {        print(s+"\n");    }        public void close() {        ps.close();    }        }
